SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_desfecho_pa ( nr_atendimento_p bigint, ie_opcao_p text ) RETURNS varchar AS $body$
DECLARE

/* Opcoes
UP 	- Data primeiro desfecho
NMI	- Nome do Medico responsavel quando desfecho for internacao
DES	- Se possui desfecho
*/
		
ds_retorno_w	varchar(255);
		

BEGIN

begin
if (ie_opcao_p = 'UP') then
	select	to_char(min(coalesce(a.dt_desfecho,a.dt_atualizacao)),'dd/mm/yyyy hh24:mi:ss')
	into STRICT	ds_retorno_w
	from	atendimento_alta a, parametro_medico p
	where	nr_atendimento = nr_atendimento_p
	and     p.cd_estabelecimento = obter_estabelecimento_ativo
    and     a.ie_tipo_orientacao = 'P'
    and     ((coalesce(p.ie_liberar_desfecho,'N')  = 'N') or ((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') and (coalesce(a.dt_inativacao::text, '') = '')));
elsif (ie_opcao_p = 'NMI') then
	select	max(obter_nome_pf(cd_medico_dest))
	into STRICT	ds_retorno_w
	from	atendimento_alta
	where	nr_sequencia = (SELECT	max(a.nr_sequencia)
				from	atendimento_alta a, parametro_medico p
				where	nr_atendimento = nr_atendimento_p
				and		ie_desfecho = 'I'
				and     p.cd_estabelecimento = obter_estabelecimento_ativo
				and     a.ie_tipo_orientacao = 'P'
                and     ((coalesce(p.ie_liberar_desfecho,'N')  = 'N') or ((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') and (coalesce(a.dt_inativacao::text, '') = ''))));
elsif (ie_opcao_p = 'DES') then
	select	CASE WHEN coalesce(count(*),0)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	atendimento_alta a, parametro_medico p
	where	nr_atendimento = nr_atendimento_p
	and     p.cd_estabelecimento = obter_estabelecimento_ativo
    and     a.ie_tipo_orientacao = 'P'
    and     ((coalesce(p.ie_liberar_desfecho,'N')  = 'N') or ((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') and (coalesce(a.dt_inativacao::text, '') = '')));
end if;
exception
when others then
	ds_retorno_w := null;
end;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
