CREATE OR REPLACE FUNCTION obter_dados_discussao_laudo ( nr_seq_laudo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(150);
ds_resultado_padrao_w	varchar(150);
dt_conclusao_w		timestamp;
nr_Seq_disc_laudo_w	bigint;


BEGIN

select  Max(SUBSTR(obter_desc_resultado_padrao(NR_SEQ_RESULTADO_PADRAO),1,255)) ds_resultado_padrao,
	Max(dt_conclusao),
	Max(nr_sequencia)
into STRICT	ds_resultado_padrao_w,
	dt_conclusao_w,
	nr_Seq_disc_laudo_w
from	DISCUSSAO_LAUDO_PATOLOGIA
where	nr_seq_laudo = nr_seq_laudo_p;

if (ie_opcao_p = 'R') then
	ds_retorno_w	:= ds_resultado_padrao_w;
elsif (ie_opcao_p = 'C') then
	ds_retorno_w	:= to_char(dt_conclusao_w,'dd/mm/yyyy');
elsif (ie_opcao_p = 'S') then
	ds_retorno_w	:= nr_Seq_disc_laudo_w;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
