CREATE OR REPLACE FUNCTION obter_dados_disp_inter_sae ( nr_seq_prescr_p bigint, nr_seq_dispositivo_p bigint, nr_seq_disp_atend_p bigint, ie_opcao_p text, nr_atendimento_p bigint, nr_seq_proc_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(255);
ie_gerar_w	varchar(1);
ds_hora_w	varchar(255);


/*
G - Gerar
H - Hora
*/
BEGIN

select 	max(ie_gerar),
		max(hr_prim_horario)
into STRICT	ie_gerar_w,
		ds_hora_w
from	dispositivo_interv_sae_alt
where	nr_seq_prescr = nr_seq_prescr_p
and		nr_seq_dispositivo = nr_seq_dispositivo_p
and		nr_seq_disp_atend = nr_seq_disp_atend_p
and		nr_atendimento = nr_atendimento_p
and		nr_seq_proc = nr_seq_proc_p;


if (ie_opcao_p = 'G') then
	ds_retorno_w	:= coalesce(ie_gerar_w,'S');

elsif (ie_opcao_p = 'H') then
	ds_retorno_w := ds_hora_w;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
