SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_dmed (nr_sequencia_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*
RVI - Respons치veis com valor informado
RVN - Respons치veis sem valor informado
QTR - Total dos respons치veis
VTR - Total dos valores dos respons치veis
QTB - Beneficiarios
VTB - Total valores pago
*/
retorno_w		double precision;


BEGIN
if (ie_opcao_p = 'RVI') then
	select 	COUNT(*)
	into STRICT	retorno_w
	from   	dmed_resp_pagamento a,
		dmed b
	where	a.nr_seq_dmed = b.nr_sequencia
	and	a.nr_seq_dmed = nr_sequencia_p
	and	(a.cd_pessoa_fisica IS NOT NULL AND a.cd_pessoa_fisica::text <> '')
	and	a.vl_pago_resp > 0;
end if;
if (ie_opcao_p = 'RVN') then
	select 	COUNT(*)
	into STRICT	retorno_w
	from   	dmed_resp_pagamento a,
		dmed b
	where	a.nr_seq_dmed = b.nr_sequencia
	and	a.nr_seq_dmed = nr_sequencia_p
	and	(a.cd_pessoa_fisica IS NOT NULL AND a.cd_pessoa_fisica::text <> '')
	and	a.vl_pago_resp = 0;
end if;
if (ie_opcao_p = 'QTR') then
	select 	COUNT(*)
	into STRICT	retorno_w
	from   	dmed_resp_pagamento a,
		dmed b
	where	a.nr_seq_dmed = b.nr_sequencia
	and	a.nr_seq_dmed = nr_sequencia_p
	and	(a.cd_pessoa_fisica IS NOT NULL AND a.cd_pessoa_fisica::text <> '');
end if;
if (ie_opcao_p = 'VTR') then
	SELECT	SUM(a.vl_pago_resp)
	into STRICT	retorno_w
	from	dmed_resp_pagamento a,
		dmed b
	where	a.nr_seq_dmed = b.nr_sequencia
	and	a.nr_seq_dmed = nr_sequencia_p
	and	(a.cd_pessoa_fisica IS NOT NULL AND a.cd_pessoa_fisica::text <> '');
end if;
if (ie_opcao_p = 'QTB') then
	select	count(*)
	into STRICT	retorno_w
	from	dmed_beneficiario_pago a
	where	a.nr_seq_dmed_resp in (SELECT	a.nr_sequencia
					from    dmed_resp_pagamento a,
						dmed b
					where   a.nr_seq_dmed = nr_sequencia_p
					and	a.nr_seq_dmed = b.nr_sequencia);
end if;
if (ie_opcao_p = 'VTB') then
	select	sum(a.vl_pago_beneficiario)
	into STRICT	retorno_w
	from	dmed_beneficiario_pago a
	where	a.nr_seq_dmed_resp in (SELECT	a.nr_sequencia
					from    dmed_resp_pagamento a,
						dmed b
					where   a.nr_seq_dmed = nr_sequencia_p
					and	a.nr_seq_dmed = b.nr_sequencia);
end if;


return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
