CREATE OR REPLACE FUNCTION obter_dados_document_relat ( nr_sequencia_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1)  := 'N';


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_retorno_w
from	relatorio_documentacao
where	nr_seq_relatorio = nr_sequencia_p
and	((cd_pf_solic	 = cd_pessoa_fisica_p AND ie_opcao_p = 'S') or
	 (cd_pf_criador = cd_pessoa_fisica_p AND ie_opcao_p = 'C'));


return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
