SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_doc_local_armaz ( nr_seq_local_p bigint, ie_informacao_p text) RETURNS varchar AS $body$
DECLARE


/* ie_informacao_p
D = Descrição do local
C = Corredor
P = Prateleira
*/
ds_retorno_w		varchar(255);
ds_local_w		doc_local_armazenamento.ds_local%type;
ds_corredor_w		doc_local_armazenamento.ds_corredor%type;
ds_prateleira_w		doc_local_armazenamento.ds_prateleira%type;


BEGIN

select	coalesce(max(ds_local),''),
	coalesce(max(ds_corredor),''),
	coalesce(max(ds_prateleira),'')
into STRICT	ds_local_w,
	ds_corredor_w,
	ds_prateleira_w
from	doc_local_armazenamento
where	nr_sequencia = nr_seq_local_p;

if (ie_informacao_p = 'D') then
	ds_retorno_w	:= ds_local_w;
elsif (ie_informacao_p = 'C') then
	ds_retorno_w	:= ds_corredor_w;
elsif (ie_informacao_p = 'P') then
	ds_retorno_w	:= ds_prateleira_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
