SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_drg (nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


cd_drg_w						drg_procedimento.cd_drg%type;
ds_drg_w						drg_procedimento.ds_drg%type;
qt_estadia_media_w				drg_procedimento.qt_estadia_media%type;
qt_estadia_min_w				drg_procedimento.qt_estadia_min%type;
qt_estadia_max_w				drg_procedimento.qt_estadia_max%type;

ds_retorno_w	varchar(2000);

/*

ie_opcao_p

C 	- CODE
D 	- DESCRIPTION
MS 	- AVERAGE OF THE LENGTH OF STAY FOR THE DRG
FDD 	-  FIRST DAY WITH DISCOUNT
FDC 	-  FIRST DAY WITH CHARGING
*/
BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	cd_drg,
		ds_drg,
		qt_estadia_media,
		qt_estadia_min,
		qt_estadia_max
	into STRICT	cd_drg_w,
		ds_drg_w,
		qt_estadia_media_w,
		qt_estadia_min_w,
		qt_estadia_max_w
	from   	drg_procedimento
	where   nr_sequencia = nr_sequencia_p;

	if (ie_opcao_p = 'C') then
		ds_retorno_w := cd_drg_w;

	elsif (ie_opcao_p = 'D') then
		ds_retorno_w := ds_drg_w;

	elsif (ie_opcao_p = 'MS') then
		ds_retorno_w := qt_estadia_media_w;

	elsif (ie_opcao_p = 'FDD') then
		ds_retorno_w := qt_estadia_min_w;

	elsif (ie_opcao_p = 'FDC') then
		ds_retorno_w := qt_estadia_max_w;

	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_drg (nr_sequencia_p bigint, ie_opcao_p text) FROM PUBLIC;

