CREATE OR REPLACE FUNCTION obter_dados_emitente ( cd_cgc_emitente_p text, ie_tipo_dado_p text) RETURNS varchar AS $body$
DECLARE


/*
CRT - IE_TIPO_CRT da tabela TIPO_TRIBUTACAO_CRT.
*/
ds_retorno_w	varchar(255);


BEGIN

if (cd_cgc_emitente_p IS NOT NULL AND cd_cgc_emitente_p::text <> '') then

	begin

	if (ie_tipo_dado_p = 'CRT') then

		select 	substr(ie_tipo_crt,1,1) ie_tipo_crt
		into STRICT	ds_retorno_w
		from 	pessoa_juridica a,
			tipo_tributacao_crt b
		where 	a.ie_tipo_tributacao = b.ie_tipo_tributacao
		and	a.cd_cgc = cd_cgc_emitente_p;

	end if;

	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
