SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_empresa ( cd_empresa_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/*ie_opcao_p 
CC - CD_CONTABILISTA 
NC - NM_CONTABILISTA		 
*/
 
	 
cd_contabilista_w		varchar(10);
nm_contabilista_w		varchar(100);
ds_retorno_w			varchar(255);
	

BEGIN 
 
select	cd_contabilista, 
	substr(obter_nome_pf(cd_contabilista),1,100) 
into STRICT	cd_contabilista_w, 
	nm_contabilista_w 
from	empresa 
where	cd_empresa = cd_empresa_p;
 
if (ie_opcao_p = 'CC') then 
	ds_retorno_w := cd_contabilista_w;
elsif (ie_opcao_p = 'NC') then 
	ds_retorno_w := nm_contabilista_w;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
