CREATE OR REPLACE FUNCTION obter_dados_emp_mat ( nr_emprestimo_p bigint, nr_seq_emp_material_p bigint, ie_opcao_p bigint) RETURNS bigint AS $body$
DECLARE


/*
1 - Quantidade devolvida acumulada
2 - Quantidade devolvida no momento
*/
qt_retorno_w	double precision;

dt_inicio_w	timestamp;
dt_fim_w		timestamp;


BEGIN
dt_inicio_w	:= trunc(clock_timestamp(), 'dd');
dt_fim_w		:= (trunc(clock_timestamp(),'dd') + 86399/86400);

begin
if (ie_opcao_p = 1) then
	select	coalesce(sum(qt_material),0)
	into STRICT	qt_retorno_w
	from	emprestimo_material_dev
	where	nr_emprestimo = nr_emprestimo_p
	and	nr_sequencia = nr_seq_emp_material_p;
elsif (ie_opcao_p = 2) then
	select	coalesce(sum(qt_material),0)
	into STRICT	qt_retorno_w
	from	emprestimo_material_dev
	where	nr_emprestimo = nr_emprestimo_p
	and	nr_sequencia = nr_seq_emp_material_p
	and	dt_atualizacao between dt_inicio_w and dt_fim_w;
end if;
exception
	when others then
		qt_retorno_w := 0;
end;

return qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
