CREATE OR REPLACE FUNCTION obter_dados_entrega_material (cd_material_p bigint, cd_estabelecimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_ordem_w		bigint;
dt_entrega_w		timestamp;


BEGIN

select	min(a.nr_ordem_compra),
	min(c.dt_prevista_entrega)
into STRICT	nr_ordem_w,
	dt_entrega_w
from	ordem_compra a,
	ordem_compra_item b,
	ordem_compra_item_entrega c
where	coalesce(c.qt_real_entrega,0)	< c.qt_prevista_entrega
and	coalesce(c.dt_cancelamento::text, '') = ''
and	c.dt_prevista_entrega > clock_timestamp()
and	coalesce(c.dt_real_entrega::text, '') = ''
and	b.nr_ordem_compra	= c.nr_ordem_compra
and	b.nr_item_oci		= c.nr_item_oci
and	b.nr_ordem_compra	= a.nr_ordem_compra
and	coalesce(a.dt_baixa::text, '') = ''
and	(a.dt_aprovacao IS NOT NULL AND a.dt_aprovacao::text <> '')
and	(a.dt_aprovacao IS NOT NULL AND a.dt_aprovacao::text <> '')
and	a.cd_estabelecimento	= cd_estabelecimento_p
and	b.cd_material in (SELECT	cd_material from material where cd_material_estoque = cd_material_p)
and	(b.dt_aprovacao IS NOT NULL AND b.dt_aprovacao::text <> '');

if (ie_opcao_p = 'N') then
	ds_retorno_w := to_char(nr_ordem_w);
elsif (ie_opcao_p = 'D') then
	ds_retorno_w := to_char(dt_entrega_w,'dd/mm/yyyy');
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
