CREATE OR REPLACE FUNCTION obter_dados_equipamento ( cd_equipamento_p bigint, ie_opcao_p text, ie_html_p text default 'N') RETURNS varchar AS $body$
DECLARE

/*
C	-	Classsificação
DC	- 	Descrição curta
*/
ds_retorno_w		varchar(255);
cd_classificacao_w	bigint;
ds_cor_w			varchar(15);
ds_cor_html_w		varchar(15);
ds_equip_curto_w	varchar(100);
cd_estabelecimento_w	smallint;
ie_grafico_disp_w	varchar(1);


BEGIN

if (cd_equipamento_p IS NOT NULL AND cd_equipamento_p::text <> '') then
	select	cd_classificacao,
		ds_cor,
		ds_equip_curto,
		cd_estabelecimento,
		ie_grafico_disp,
		coalesce(ds_cor_html,'#000')
	into STRICT	cd_classificacao_w,
		ds_cor_w,
		ds_equip_curto_w,
		cd_estabelecimento_w,
		ie_grafico_disp_w,
		ds_cor_html_w
	from	equipamento
	where	cd_equipamento	=	cd_equipamento_p;

	if (ie_opcao_p = 'C') then
		ds_retorno_w	:= to_char(cd_classificacao_w);
	elsif (ie_opcao_p = 'COR') then
		if (ie_html_p = 'S') then
			ds_cor_w :=  ds_cor_html_w;
		end if;
		ds_retorno_w	:= coalesce(ds_cor_w,'clBlack');
	elsif (ie_opcao_p = 'DC') then
		ds_retorno_w	:= ds_equip_curto_w;
	elsif (ie_opcao_p = 'E') then
		ds_retorno_w	:= cd_estabelecimento_w;
	elsif (ie_opcao_p = 'GD') then
		ds_retorno_w	:= ie_grafico_disp_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
