SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_escala_braden ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	IE_OPCAO_P
DR = Primeiro registro
DA = Ultimo registro antes da data dt_evento_w
*/
dt_registro_w		varchar(50);
ds_resultado_w		varchar(255);
ds_retorno_w		varchar(255);
dt_evento_w			timestamp;
nr_atendimento_w	bigint;
qt_ponto_w			varchar(4);


BEGIN
select  	max(a.dt_evento),
	max(a.nr_atendimento)
into STRICT	dt_evento_w,
	nr_atendimento_w
from	qua_evento_paciente a,
	cur_ferida b
where	b.nr_seq_evento = a.nr_sequencia
and	b.nr_sequencia  = nr_sequencia_p;

if (ie_opcao_p = 'DR') and (nr_atendimento_w > 0) then
	select	max(to_char(dt_avaliacao,'dd/mm/yyyy hh24:mi:ss')),
		max(substr(obter_resultado_braden(qt_ponto),1,255)),
		max(to_char(qt_ponto))
	into STRICT	dt_registro_w,
		ds_resultado_w,
		qt_ponto_w
	from	atend_escala_braden
	where	nr_sequencia = (SELECT	min(nr_sequencia)
				from	atend_escala_braden x
				where	x.nr_atendimento = nr_atendimento_w
				and	(x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '')
				and	coalesce(x.dt_inativacao::text, '') = ''	);

	ds_retorno_w := dt_registro_w || ' - ' ||qt_ponto_w ||obter_desc_expressao(296032)/*' pontos - '*/||ds_resultado_w;

elsif (ie_opcao_p = 'DA') and (nr_atendimento_w > 0) and (dt_evento_w IS NOT NULL AND dt_evento_w::text <> '') then
	select	max(to_char(dt_avaliacao,'dd/mm/yyyy hh24:mi:ss')),
		max(substr(obter_resultado_braden(qt_ponto),1,255)),
		max(to_char(qt_ponto))
	into STRICT	dt_registro_w,
		ds_resultado_w,
		qt_ponto_w
	from	atend_escala_braden
	where	nr_sequencia = (SELECT	max(nr_sequencia)
				from	atend_escala_braden x
				where	x.nr_atendimento = nr_atendimento_w
				and	x.dt_avaliacao < dt_evento_w
				and	(x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '')
				and	coalesce(x.dt_inativacao::text, '') = '');

	ds_retorno_w := dt_registro_w || ' - ' ||qt_ponto_w ||obter_desc_expressao(296032)/*' pontos - '*/||ds_resultado_w;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
