CREATE OR REPLACE FUNCTION obter_dados_escala_diaria_adic (nr_seq_escala_adic_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_escala_w		bigint;
ds_retorno_w		varchar(255);


BEGIN

select 	max(nr_seq_escala)
into STRICT	nr_seq_escala_w
from	escala_diaria a,
	escala_diaria_adic b
where	b.nr_seq_escala_diaria = a.nr_sequencia
and	b.nr_sequencia = nr_seq_escala_adic_p;

if (ie_opcao_p = 'E') then
	ds_retorno_w := nr_seq_escala_w;
end if;


return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
