SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_escala_prm ( nr_sequencia_p bigint, ie_opcao_p text, dt_inicio_p timestamp, dt_final_p timestamp) RETURNS varchar AS $body$
DECLARE

/* 
√Årea de origem	'A' 
Tipo de problema	'P' 
Tipo de evento	'E' 
Fator		'F' 
Total de PRM	'T' 
Total Pacientes Dia	'PD' 
 
*/
			 
ds_retorno_w	varchar(255);
	

BEGIN 
 
If (ie_opcao_p = 'A') then 
 
	select substr(ds_area,1,255) 
	into STRICT 	ds_retorno_w 
	from  	prm_area_origem 
	where 	ie_situacao = 'A' 
	and  	nr_sequencia = nr_sequencia_p;
	 
elsif (ie_opcao_p = 'P') then 
 
	select substr(ds_problema,1,255) 
	into STRICT 	ds_retorno_w 
	from	prm_tipo_problema 
	where	nr_sequencia = nr_sequencia_p;
	 
elsif (ie_opcao_p = 'E') then 
 
	select substr(ds_tipo_evento,1,255) 
	into STRICT 	ds_retorno_w 
	from	qua_tipo_evento 
	where	nr_sequencia = nr_sequencia_p;
	 
elsif (ie_opcao_p = 'F') then 
 
	select substr(ds_fator,1,255) 
	into STRICT 	ds_retorno_w 
	from	prm_fator_contribuinte 
	where	nr_sequencia = nr_sequencia_p;
	 
elsif (ie_opcao_p = 'T') then 
	 
	if (coalesce(dt_final_p::text, '') = '') then 
		select 	count(*) 
		into STRICT	ds_retorno_w	 
		from	qua_evento_paciente b, 
			PRM_FICHA_OCORRENCIA e 
		where	e.nr_seq_evento = b.nr_sequencia 
		and 	coalesce(b.dt_inativacao::text, '') = '' 
		and	trunc(b.dt_evento) = trunc(dt_inicio_p);
	end if;
 
elsif (ie_opcao_p = 'PD') then 
 
	select	count(distinct nr_atendimento) 
	into STRICT	ds_retorno_w 
	from	resumo_atendimento_paciente_v 
	where	trunc(dt_entrada) <= trunc(dt_inicio_p) 
	and	coalesce(dt_alta,fim_dia(dt_inicio_p)) >= fim_dia(dt_inicio_p);
	 
end if;
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_escala_prm ( nr_sequencia_p bigint, ie_opcao_p text, dt_inicio_p timestamp, dt_final_p timestamp) FROM PUBLIC;

