SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_escala ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	ie_opcao
	D - Descrição da escala
	C - Classificação da escala
	G - Grupo da escala
	SC - Sequência da classificação da escala
	SG - Sequência do grupo da escala		*/
ds_retorno_w		varchar(255);
ds_escala_w		varchar(60);
ds_classificacao_w		varchar(60);
ds_grupo_w		varchar(60);
nr_seq_classif_w		bigint;
nr_seq_grupo_w		bigint;


BEGIN

if (coalesce(nr_sequencia_p,0) > 0) then
	begin
	select	a.ds_escala,
		c.ds_classificacao,
		b.ds_grupo,
		b.nr_seq_classif,
		a.nr_seq_grupo
	into STRICT	ds_escala_w,
		ds_classificacao_w,
		ds_grupo_w,
		nr_seq_classif_w,
		nr_seq_grupo_w
	from	escala_classif c,
		escala_grupo b,
		escala a
	where	c.nr_sequencia = b.nr_seq_classif
	and	b.nr_sequencia = a.nr_seq_grupo
	and	a.nr_sequencia = nr_sequencia_p;

	if (ie_opcao_p = 'D') then
		ds_retorno_w := ds_escala_w;
	elsif (ie_opcao_p = 'C') then
		ds_retorno_w := ds_classificacao_w;
	elsif (ie_opcao_p = 'G') then
		ds_retorno_w := ds_grupo_w;
	elsif (ie_opcao_p = 'SC') then
		ds_retorno_w := nr_seq_classif_w;
	elsif (ie_opcao_p = 'SG') then
		ds_retorno_w := nr_seq_grupo_w;
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
