CREATE OR REPLACE FUNCTION obter_dados_estab ( cd_estabelecimento_p bigint, ie_retorno_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);

/*
1 - Nome fantasia
2- √Årea do estabelecimento
3- Sigla Gratuidade
4- CD Interno
*/
BEGIN

if (ie_retorno_p = 1) then

	select	max(nm_fantasia_estab)
	into STRICT	ds_retorno_w
	from	estabelecimento
	where	cd_estabelecimento	= cd_estabelecimento_p;

elsif (ie_retorno_p = 2) then

	select	substr(obter_valor_dominio(4582,ie_area_estab),1,255)
	into STRICT	ds_retorno_w
	from	estabelecimento
	where	cd_estabelecimento	= cd_estabelecimento_p;

elsif (ie_retorno_p = 3) then

	select	substr(ds_sigla_gratuidade,1,255)
	into STRICT	ds_retorno_w
	from	estabelecimento
	where	cd_estabelecimento	= cd_estabelecimento_p;

elsif (ie_retorno_p = 4) then
	select	cd_interno
	into STRICT	ds_retorno_w
	from	estabelecimento
	where	cd_estabelecimento	= cd_estabelecimento_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_estab ( cd_estabelecimento_p bigint, ie_retorno_p bigint) FROM PUBLIC;

