CREATE OR REPLACE FUNCTION obter_dados_estagio_dia_ciclo ( nr_seq_atendimento_p bigint, nr_ciclo_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


/*ie_opcao_p
	1 = Descrição do estágio
	2 = Cor do estágio*/
nr_seq_estagio_w	bigint;
ds_retorno_w		varchar(255);
ie_interno_w		varchar(10);
nr_seq_paciente_w	bigint;
ds_cor_w			varchar(255);
nr_ciclo_w			smallint;
ds_dia_ciclo_w		varchar(5);
ds_estagio_w		varchar(255);


BEGIN
/*
select	max(nr_seq_paciente)
into	nr_seq_paciente_w
from	paciente_atendimento
where	nr_seq_atendimento = nr_seq_atendimento_p;


select	max(nr_seq_estagio)
into	nr_seq_estagio_w
from	autorizacao_convenio
where	nr_seq_paciente  = nr_seq_paciente_w;*/
--if	(nvl(nr_seq_estagio_w,0) = 0) and
--	(nvl(nr_seq_paciente_w,0) > 0)then
	select	max(nr_ciclo),
			max(ds_dia_ciclo),
			max(nr_seq_paciente)
	into STRICT	nr_ciclo_w,
			ds_dia_ciclo_w,
			nr_seq_paciente_w
	from	paciente_atendimento
	where	nr_seq_atendimento = nr_seq_atendimento_p;

	select	max(b.ds_cor),
		max(b.ds_estagio)
	into STRICT	ds_cor_w,
		ds_estagio_w
	from	autorizacao_convenio a,
		estagio_autorizacao b
	where	a.nr_ciclo 			= nr_ciclo_w
	and	((a.ds_dia_ciclo 		= ds_dia_ciclo_w) or (coalesce(a.ds_dia_ciclo, 'X') 	= 'X'))
	and	a.nr_seq_paciente_setor 	= nr_seq_paciente_w
	and	a.nr_seq_estagio		= b.nr_sequencia
	and	b.ie_interno 			not in ('90','70');

	if (coalesce(ds_cor_w::text, '') = '') then

		select	max(b.ds_cor),
			max(b.ds_estagio)
		into STRICT	ds_cor_w,
			ds_estagio_w
		from	autorizacao_convenio a,
			estagio_autorizacao b
		where	a.nr_ciclo 			= nr_ciclo_w
		and	((a.ds_dia_ciclo 		= ds_dia_ciclo_w) or (coalesce(a.ds_dia_ciclo, 'X') 	= 'X'))
		and	a.nr_seq_paciente_setor 	= nr_seq_paciente_w
		and	a.nr_seq_estagio		= b.nr_sequencia;
	end if;

	if (ie_opcao_p = 1) then
		ds_retorno_w := ds_estagio_w;
		/*select	max(ds_estagio)
		into	ds_retorno_w
		from	estagio_autorizacao
		where	nr_sequencia = nr_seq_estagio_w;*/
	elsif (ie_opcao_p = 2) then
		ds_retorno_w := ds_cor_w;
		/*select	max(ds_cor)
		into	ds_retorno_w
		from	estagio_autorizacao
		where	nr_sequencia = nr_seq_estagio_w;*/
	end if;

--end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
