CREATE OR REPLACE FUNCTION obter_dados_estrut_proc ( cd_procedimento_p bigint, ie_origem_proced_p bigint, ie_opcao_p text, ie_estrutura_p text) RETURNS varchar AS $body$
DECLARE


cd_area_proc_w		bigint;
ds_area_proc_w		varchar(40);
cd_grupo_proc_w		bigint;
ds_grupo_proc_w		varchar(240);
cd_especialidade_w	bigint;
ds_especialidade_w	varchar(240);


BEGIN
select	cd_area_procedimento,
	ds_area_procedimento,
	cd_grupo_proc,
	ds_grupo_proc,
	cd_especialidade,
	ds_especialidade
into STRICT	cd_area_proc_w,
	ds_area_proc_w,
	cd_grupo_proc_w,
	ds_grupo_proc_w,
	cd_especialidade_w,
	ds_especialidade_w
from	estrutura_procedimento_v
where	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p;

if (ie_opcao_p = 'C') then
	if (ie_estrutura_p = 'A') then
		RETURN cd_area_proc_w;
	elsif (ie_estrutura_p = 'G') then
		RETURN cd_grupo_proc_w;
	elsif (ie_estrutura_p = 'E') then
		RETURN cd_especialidade_w;
	end if;
elsif (ie_opcao_p = 'D') then
	if (ie_estrutura_p = 'A') then
		RETURN	ds_area_proc_w;
	elsif (ie_estrutura_p = 'G') then
		RETURN	ds_grupo_proc_w;
	elsif (ie_estrutura_p = 'E') then
		RETURN	ds_especialidade_w;
	end if;
end if;

END;
