SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_evento_cirurgia ( nr_sequencia_p bigint, ie_opcao_p text ) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p para customizações
IC	Inicia cirurgia
SE	Sequência de execução
*/
ds_retorno_w		varchar(15);
nr_seq_execucao_w	bigint;


BEGIN

if (nr_sequencia_p > 0) then
	select	nr_seq_execucao
	into STRICT	nr_seq_execucao_w
	from	evento_cirurgia
	where	nr_sequencia = nr_sequencia_p;

	if (ie_opcao_p = 'IC') then
		select	coalesce(max(ie_inicia_cirurgia),'N')
		into STRICT	ds_retorno_w
		from	evento_cirurgia
		where	nr_sequencia	=	nr_sequencia_p;
	elsif (ie_opcao_p = 'SE') then
		ds_retorno_w	:= to_char(nr_seq_execucao_w);
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
