CREATE OR REPLACE FUNCTION obter_dados_evento_disp ( nr_seq_disp_pac_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_evento_w	qua_evento_paciente.ds_evento%type;


BEGIN

if (coalesce(nr_seq_disp_pac_p,0) > 0) then
	if (ie_opcao_p = 'DE') then
		select	max(ds_evento)
		into STRICT		ds_evento_w
		from		qua_evento_paciente
		where		nr_seq_disp_pac = nr_seq_disp_pac_p
		and		((dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') or (nm_usuario = wheb_usuario_pck.get_nm_usuario));
	end if;
end if;

return	ds_evento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
