CREATE OR REPLACE FUNCTION obter_dados_evento_sol_adep (ie_tipo_solucao_p bigint, nr_prescricao_p bigint, nr_seq_solucao_p bigint, ie_evento_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


vl_retorno_w		varchar(255);
nr_seq_evento_w	bigint;
dt_evento_w		varchar(19);
cd_pessoa_evento_w	varchar(10);


BEGIN
if (ie_tipo_solucao_p IS NOT NULL AND ie_tipo_solucao_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') and (ie_evento_p IS NOT NULL AND ie_evento_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then
	/* obter evento */

	select	coalesce(obter_evento_adep_solucao(ie_tipo_solucao_p, nr_prescricao_p, nr_seq_solucao_p, ie_evento_p),0)
	into STRICT	nr_seq_evento_w
	;

	/* obter dados */

	if (nr_seq_evento_w > 0) then
		select	to_char(dt_alteracao,'dd/mm/yyyy hh24:mi:ss') dt_evento,
			cd_pessoa_fisica
		into STRICT	dt_evento_w,
			cd_pessoa_evento_w
		from	prescr_solucao_evento
		where	nr_sequencia = nr_seq_evento_w;


		/* definir opcao */

		if (ie_opcao_p = 'DE') then
			vl_retorno_w := dt_evento_w;
		elsif (ie_opcao_p = 'PE') then
			vl_retorno_w := cd_pessoa_evento_w;
		end if;
	end if;
end if;

return vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
