CREATE OR REPLACE FUNCTION obter_dados_exames_comp_pato (nr_seq_exame_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(255);

BEGIN

if (ie_tipo_p = 'A' ) then
	select	coalesce(ie_possui_anticorpos,'N')
	into STRICT	ds_retorno_w
	from	patologia_exame_compl
	where	nr_sequencia = nr_seq_exame_p;
elsif (ie_tipo_p = 'D') then
	select	substr(ds_exame_compl,1,255)
	into STRICT	ds_retorno_w
	from	patologia_exame_compl
	where	nr_sequencia = nr_seq_exame_p;
end if;



return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
