CREATE OR REPLACE FUNCTION obter_dados_exame_lab_lib ( cd_exame_p text, ie_opcao_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


/*	ie_opcao_p
	C - CÃ³digo
	S - Sequencia
*/
cd_exame_w	varchar(20);
nr_seq_exame_w	bigint;
ds_retorno_w	varchar(20);


BEGIN

select	cd_exame,
	nr_seq_exame
into STRICT	cd_exame_w,
	nr_seq_exame_w
from	exame_laboratorio
where	upper(cd_exame)	= upper(cd_exame_p)
and	substr(lab_obter_se_exame_lib(nr_seq_exame, cd_estabelecimento_p), 1,1) = 'S'
and	ie_solicitacao	= 'S';

if (ie_opcao_p = 'C') then
	ds_retorno_w	:= cd_exame_w;
elsif (ie_opcao_p = 'S') then
	ds_retorno_w	:= nr_seq_exame_w;
end if;

return	ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
