CREATE OR REPLACE FUNCTION obter_dados_exame_lab_rep ( cd_perfil_p bigint, cd_especialidade_p bigint, cd_setor_atendimento_p bigint, nr_seq_exame_p bigint, cd_estabelecimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000);
ie_regra_novo_w		varchar(20);
cd_intervalo_w		varchar(15);
ds_orientacao_w		varchar(255);
ie_copia_rep_w		varchar(20);
qt_limite_solic_w	double precision;
cd_material_exame_w	varchar(50);
hr_prev_execucao_w	varchar(5);

c01 CURSOR FOR
SELECT	ie_regra_novo,
	cd_intervalo,
	ds_orientacao,
	ie_copia_rep,
	qt_limite_solic,
	to_char(hr_prev_execucao,'hh24:mi')
from	exame_lab_rep
where	nr_seq_exame		= nr_seq_exame_p
and	coalesce(cd_setor_atendimento,cd_setor_atendimento_p)	= cd_setor_atendimento_p
and	coalesce(cd_estabelecimento,cd_estabelecimento_p)		= cd_estabelecimento_p
and	coalesce(cd_perfil,cd_perfil_p)				= cd_perfil_p
and	coalesce(cd_especialidade, coalesce(cd_especialidade_p,0)) = coalesce(cd_especialidade_p,0)
order by coalesce(cd_setor_atendimento,0);


BEGIN

open c01;
loop
fetch c01 into
	ie_regra_novo_w,
	cd_intervalo_w,
	ds_orientacao_w,
	ie_copia_rep_w,
	qt_limite_solic_w,
	hr_prev_execucao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;

if (ie_opcao_p	= 'N') then
	ds_retorno_w	:= ie_regra_novo_w;
elsif (ie_opcao_p	= 'I') then
	ds_retorno_w	:= cd_intervalo_w;
elsif (ie_opcao_p	= 'O') then
	ds_retorno_w	:= ds_orientacao_w;
elsif (ie_opcao_p	= 'C') then
	ds_retorno_w	:= ie_copia_rep_w;
elsif (ie_opcao_p	= 'Q') then
	ds_retorno_w	:= qt_limite_solic_w;
elsif (ie_opcao_p	= 'H') then
	ds_retorno_w	:= hr_prev_execucao_w;
end if;

return ds_retorno_w;

end;
