SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_faltantes_nfse (nr_seq_nota_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w       		varchar(4000) := '';
ds_endereco_w					varchar(255);
cd_cep_w						varchar(255);
cd_cgc_w						varchar(255);
cd_cpf_w						varchar(255);
cd_inscricao_municipal_w		varchar(255);
nm_pessoa_w						varchar(255);
sg_estado_w						varchar(255);
cd_pessoa_fisica_w				varchar(255);
				

BEGIN 
 
 
select	substr(obter_dados_pf_pj(a.cd_pessoa_fisica, a.cd_cgc, 'EN'), 1, 255), -- endereço. 
	substr(obter_dados_pf_pj(a.cd_pessoa_fisica, a.cd_cgc, 'CEP'), 1, 255), -- CEP 
	substr(obter_dados_pf_pj(a.cd_pessoa_fisica, a.cd_cgc, 'C'), 1, 255), -- CNPJ 
	substr(obter_cpf_pessoa_fisica(a.cd_pessoa_fisica), 1, 255), -- cpf 
	substr(obter_dados_pf_pj(a.cd_pessoa_fisica, a.cd_cgc, 'IM'), 1, 255), -- inscrição municipal 
	substr(obter_nome_pf_pj(a.cd_pessoa_fisica, a.cd_cgc), 1, 255), -- razao social da empresa ou nome pessoa fisica. 
	substr(obter_dados_pf_pj(a.cd_pessoa_fisica, a.cd_cgc, 'UF'), 1, 255), -- sigla do Estado 
	substr(a.cd_pessoa_fisica, 1, 255) 
into STRICT	ds_endereco_w, 
	cd_cep_w, 
	cd_cgc_w, 
	cd_cpf_w, 
	cd_inscricao_municipal_w, 
	nm_pessoa_w, 
	sg_estado_w, 
	cd_pessoa_fisica_w 
from	nota_fiscal a 
where	nr_sequencia	= nr_seq_nota_p;
 
 
 
if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then 
	 
	if (coalesce(nm_pessoa_w::text, '') = '' ) then 
		ds_retorno_w := ds_retorno_w || obter_desc_expressao(781271)/*' Pessoa Fisica sem nome.'*/
 || '|';
	end if;
	 
	if (coalesce(cd_cpf_w::text, '') = '' ) then 
		ds_retorno_w := ds_retorno_w || obter_desc_expressao(781279)/*' Pessoa Fisica sem CPF.'*/
 || '|';
	end if;
	 
	if (coalesce(ds_endereco_w::text, '') = '' ) then 
		ds_retorno_w := ds_retorno_w || obter_desc_expressao(781283)/*' Pessoa Fisica sem endereço.'*/
 || '|';
	end if;
	 
	if (coalesce(cd_cep_w::text, '') = '' ) then 
		ds_retorno_w := ds_retorno_w || obter_desc_expressao(781287)/*' Pessoa Fisica sem CEP cadastrado.'*/
 || '|';
	end if;
	 
	if (coalesce(sg_estado_w::text, '') = '' ) then 
		ds_retorno_w := ds_retorno_w || obter_desc_expressao(781267)/*' Pessoa Fisica sem estado cadastrado.'*/
 || '|';
	end if;
 
else 
	if (coalesce(nm_pessoa_w::text, '') = '' ) then 
		ds_retorno_w := ds_retorno_w || obter_desc_expressao(781291)/*' Pessoa Juridica sem razão social.'*/
 || '|';	
	end if;
	 
	if (coalesce(cd_cgc_w::text, '') = '' ) then 
		ds_retorno_w := ds_retorno_w || obter_desc_expressao(781293)/*' Pessoa Juridica sem CNPJ.'*/
 || '|';	
	end if;
	 
	if (coalesce(ds_endereco_w::text, '') = '' ) then 
		ds_retorno_w := ds_retorno_w || obter_desc_expressao(781273)/*'Pessoa Juridica sem endereço.'*/
 || '|';	
	end if;
	 
	 
	if (coalesce(cd_cep_w::text, '') = '' ) then	 
		ds_retorno_w := ds_retorno_w || obter_desc_expressao(781289)/*'Pessoa Juridica sem CEP cadastrado.'*/
 || '|';	
	end if;
	 
	if (coalesce(sg_estado_w::text, '') = '') then 
		ds_retorno_w := ds_retorno_w || obter_desc_expressao(781285)/*'Pessoa Juridica sem estado cadastrado.'*/
 || '|';
	end if;
 
	if (coalesce(cd_inscricao_municipal_w::text, '') = '' ) then	 
		ds_retorno_w := ds_retorno_w || obter_desc_expressao(781277)/*' Pessoa Juridica sem inscrição municipal.'*/
 || '|';	
	end if;
	 
end if;
 
return	ds_retorno_w;
 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_faltantes_nfse (nr_seq_nota_p bigint) FROM PUBLIC;

