SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_fanep ( nr_seq_pepo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


dt_agenda_w				timestamp;
nr_minuto_duracao_w		varchar(255);
ds_exame_w 				varchar(255);
ds_retorno_w			varchar(255);



BEGIN

if (nr_seq_pepo_p IS NOT NULL AND nr_seq_pepo_p::text <> '') then

	SELECT	MAX(hr_inicio) dt_agenda,
			MAX(nr_minuto_duracao) nr_minuto_duracao,
			SUBSTR(obter_exame_agenda(MAX(cd_procedimento),MAX(ie_origem_proced), MAX(nr_seq_proc_interno)),1,240) ds_exame
	into STRICT    dt_agenda_w,
			nr_minuto_duracao_w,
			ds_exame_w
	FROM 	agenda_paciente
	WHERE 	nr_seq_pepo = nr_seq_pepo_p;

end if;

if ( ie_opcao_p = 'DT') then
	ds_retorno_w	:= to_char(dt_agenda_w,'dd/mm/yyyy hh24:mi:ss');
end if;

if ( ie_opcao_p = 'NR') then
	ds_retorno_w	:= nr_minuto_duracao_w;
end if;

if ( ie_opcao_p = 'DS') then
	ds_retorno_w	:= ds_exame_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_fanep ( nr_seq_pepo_p bigint, ie_opcao_p text) FROM PUBLIC;

