SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_ficha_upp ( nr_seq_ferida_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*
Estadiamento Máximo  Grau 	'EM'
Estadiamento Último   Grau	'EU'
*/
ie_grau_ferida_w 	bigint;
ds_retorno_w	varchar(50);


BEGIN


if (ie_opcao_p = 'EM') then

	select	max(ie_grau_ferida)
	into STRICT	ie_grau_ferida_w
	from	cur_curativo
	where	nr_seq_ferida = nr_seq_ferida_p;

	If (ie_grau_ferida_w = 1) then
		ds_retorno_w:= wheb_mensagem_pck.get_texto(308090) || ' I'; -- Estágio
	elsif (ie_grau_ferida_w = 2) then
		ds_retorno_w:= wheb_mensagem_pck.get_texto(308090) || ' II'; -- Estágio
	elsif (ie_grau_ferida_w = 3) then
		ds_retorno_w:= wheb_mensagem_pck.get_texto(308090) || ' III'; -- Estágio
	elsif (ie_grau_ferida_w = 4) then
		ds_retorno_w:= wheb_mensagem_pck.get_texto(308090) || ' IV'; -- Estágio
	end if;
end if;

if (ie_opcao_p = 'EU') then

	select	ie_grau_ferida
	into STRICT	ie_grau_ferida_w
	from	cur_curativo
	where	nr_sequencia = (SELECT	max(nr_sequencia)
				from	cur_curativo
				where	nr_seq_ferida = nr_seq_ferida_p);

	If (ie_grau_ferida_w = 1) then
		ds_retorno_w:= wheb_mensagem_pck.get_texto(308090) || ' I'; -- Estágio
	elsif (ie_grau_ferida_w = 2) then
		ds_retorno_w:= wheb_mensagem_pck.get_texto(308090) || ' II'; -- Estágio
	elsif (ie_grau_ferida_w = 3) then
		ds_retorno_w:= wheb_mensagem_pck.get_texto(308090) || ' III'; -- Estágio
	elsif (ie_grau_ferida_w = 4) then
		ds_retorno_w:= wheb_mensagem_pck.get_texto(308090) || ' IV'; -- Estágio
	end if;
end if;



return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
