SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_fila_triagem ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
DS - Descrição fila triagem do pronto atendimento
*/
ds_retorno_w		varchar(255) := '';


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	if (ie_opcao_p = 'DS') then

	Select  max(b.ds_fila)
	into STRICT	ds_retorno_w
	from	triagem_pronto_atend a,
			fila_espera_senha b
	where	a.nr_atendimento = nr_atendimento_p
	and		a.NR_SEQ_PAC_FILA = b.nr_sequencia;

	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
