CREATE OR REPLACE FUNCTION obter_dados_fonte_orc_nc ( nr_ordem_compra_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*ie_opcao_p
CF - Códugo da fonte
CO - Código do orçamento
CU - Código da unidade
DF - Descrição da fonte
DO - Descrição do orçamento
DU - Descrição da unidade
*/
nr_seq_fonte_w			bigint;
nr_seq_orcamento_w		bigint;
nr_seq_unidade_w			bigint;
ds_retorno_w			varchar(255);


BEGIN


select	max(nr_seq_fonte),
	max(nr_seq_orcamento),
	max(nr_seq_unidade)
into STRICT	nr_seq_fonte_w,
	nr_seq_orcamento_w,
	nr_seq_unidade_w
from	ordem_compra_fonte_orc
where	nr_ordem_compra = nr_ordem_compra_p;

if (ie_opcao_p = 'CF') then
	ds_retorno_w := nr_seq_fonte_w;
elsif (ie_opcao_p = 'CO') then
	ds_retorno_w := nr_seq_orcamento_w;
elsif (ie_opcao_p = 'CU') then
	ds_retorno_w := nr_seq_unidade_w;
elsif (ie_opcao_p = 'DF') and (nr_seq_fonte_w > 0) then
	ds_retorno_w := substr(obter_desc_fonte_recurso(nr_seq_fonte_w,'CD'),1,255);
elsif (ie_opcao_p = 'DO') and (nr_seq_orcamento_w > 0) then
	ds_retorno_w := substr(obter_desc_orcamento_compras(nr_seq_orcamento_w,'CD'),1,255);
elsif (ie_opcao_p = 'DU') and (nr_seq_unidade_w > 0) then
	ds_retorno_w := substr(obter_desc_unid_orcamentaria(nr_seq_unidade_w,'CD'),1,255);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
