CREATE OR REPLACE FUNCTION obter_dados_forma_repasse ( cd_regra_p bigint, nr_seq_item_p bigint, ie_opcao_p text ) RETURNS varchar AS $body$
DECLARE


ds_resultado_w	varchar(255) := ' ';

/*

ie_opcao_p

'S' -> IE_PERC_SALDO

*/
BEGIN

if (ie_opcao_p = 'S') then

	select	coalesce(max(ie_perc_saldo), 'S')
	into STRICT	ds_resultado_w
	from	regra_repasse_terc_item
	where	cd_regra		= cd_regra_p
	and	nr_seq_item	= nr_seq_item_p;

end if;

return	ds_resultado_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
