CREATE OR REPLACE FUNCTION obter_dados_frac ( nr_seq_frac_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_obs_preparo_w	varchar(255);


BEGIN

if (nr_seq_frac_p > 0) then
	select	ds_obs_preparo
	into STRICT	ds_obs_preparo_w
	from	adep_processo_frac
	where	nr_sequencia = nr_seq_frac_p;
end if;

if (ie_opcao_p = 'O') then
	ds_retorno_w := ds_obs_preparo_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
