CREATE OR REPLACE FUNCTION obter_dados_funcoes_html5 ( nr_seq_funcao_p bigint, ie_tipo_dado_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);


BEGIN

if (ie_tipo_dado_p = 'F') then /* Nome da função */
	select	substr(obter_desc_funcao(a.cd_funcao),1,255)
	into STRICT	ds_retorno_w
	from	funcoes_html5 a
	where	a.nr_sequencia	= nr_seq_funcao_p;
end if;

return	ds_retorno_w;

end;
