CREATE OR REPLACE FUNCTION obter_dados_gestao_documentos ( nr_seq_documento_p bigint, nr_seq_item_doc_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(255);


BEGIN

/*
ie_opcao_p = 'I' = NR_SEQ_INTERNO
ie_opcao_p = 'C' = DS_CAIXA
*/
if (ie_opcao_p = 'I') then
	begin

	select	max(nr_seq_interno)
	into STRICT	ds_retorno_w
	from	gestao_doc_item
	where	nr_sequencia 		= nr_seq_item_doc_p
	and	nr_seq_gestao_doc	= nr_seq_documento_p;

	end;
elsif (ie_opcao_p = 'C') then
	begin

	select	max(ds_documento)
	into STRICT	ds_retorno_w
	from	gestao_documento
	where	nr_sequencia	= nr_seq_documento_p;

	end;
end if;

return	substr(ds_retorno_w,1,255);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
