CREATE OR REPLACE FUNCTION obter_dados_gestao_vaga (nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
ds_unidade_desejada_w	varchar(20);
ds_retorno_w		varchar(255);
cd_setor_desejado_w	integer;
cd_pessoa_fisica_w	varchar(10);
cd_medico_w		varchar(10);

/* 
SD	- Setor desejado 
UD	- Unidade desejada 
NP	- Nome do paciente 
MS	- MÃ©dico solicitante 
*/
 
 

BEGIN 
 
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then 
	select	cd_setor_desejado, 
		substr(cd_unidade_basica ||' '||cd_unidade_compl,1,20), 
		cd_pessoa_fisica, 
		cd_medico 
	into STRICT	cd_setor_desejado_w, 
		ds_unidade_desejada_w, 
		cd_pessoa_fisica_w, 
		cd_medico_w 
	from	gestao_vaga a 
	where	a.nr_sequencia = nr_sequencia_p;
 
	if (ie_opcao_p = 'SD') and (cd_setor_desejado_w IS NOT NULL AND cd_setor_desejado_w::text <> '') then 
		ds_retorno_w	:= substr(obter_nome_setor(cd_setor_desejado_w),1,100);
	elsif (ie_opcao_p = 'UD') then 
		ds_retorno_w	:= ds_unidade_desejada_w;
	elsif (ie_opcao_p = 'NP') and (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then 
		ds_retorno_w	:= substr(obter_nome_pf(cd_pessoa_fisica_w),1,100);
	elsif (ie_opcao_p = 'MS') and (cd_medico_w IS NOT NULL AND cd_medico_w::text <> '') then 
		ds_retorno_w	:= substr(obteR_nome_pf(cd_medico_w),1,100);
	end if;
end if;
 
RETURN	ds_retorno_w;
 
END	;
