SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_gest_con (nr_seq_nivel_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		double precision;
nr_seq_nivel_w		bigint;
cd_consultor_w		bigint;
pr_conhecimento_w	double precision;


BEGIN

select	distinct
	nr_seq_nivel,
	count(cd_consultor),
	(sum(pr_conhecimento)/count(cd_consultor))
into STRICT	nr_seq_nivel_w,
	cd_consultor_w,
	pr_conhecimento_w
from (SELECT	distinct
		a.nr_seq_nivel,
		a.cd_consultor,
		con_pr_conhecimento_tasy(a.cd_consultor,'C') pr_conhecimento
	 from	proj_consultor_nivel a) alias5
where	cd_consultor in (select	distinct
				a.cd_pessoa_fisica cd_consultor
			 from	pessoa_fisica b,
				pessoa_fisica_avaliacao c,
				com_canal_consultor a
			 where	a.cd_pessoa_fisica      = b.cd_pessoa_fisica
			 and	a.cd_pessoa_fisica = c.cd_pessoa_fisica
			 and	c.nr_sequencia = (	select   max(x.nr_sequencia)
							from     pessoa_fisica_avaliacao x
							where    x.cd_pessoa_fisica = a.cd_pessoa_fisica)
			 and	a.ie_situacao		= 'A'
			 and	a.ie_consultor_wheb	= 'S')
and	nr_seq_nivel = nr_seq_nivel_p
group by nr_seq_nivel;

if (ie_opcao_p = 'N') then
vl_retorno_w := nr_seq_nivel_w;
end if;

if (ie_opcao_p = 'C') then
vl_retorno_w := cd_consultor_w;
end if;

if (ie_opcao_p = 'P') then
vl_retorno_w := pr_conhecimento_w;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
