SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_grupo_ent_atend ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/* 
R - Código da pessoa responsável 
D - Descrição do grupo 
C - Código do grupo 
*/
 
					 
nr_sequencia_w		bigint;
nr_seq_grupo_w		bigint;
cd_pessoa_resp_w	varchar(10);
ds_grupo_w		varchar(80);
ds_retorno_w		varchar(80);
					

BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
 
	select	max(nr_seq_grupo) 
	into STRICT	nr_seq_grupo_w 
	from	san_entidade 
	where	nr_sequencia = san_obter_entidade_atend(nr_atendimento_p);
 
	if (nr_seq_grupo_w IS NOT NULL AND nr_seq_grupo_w::text <> '') then 
		select	nr_sequencia, 
			cd_pessoa_fisica, 
			ds_grupo 
		into STRICT	nr_sequencia_w, 
			cd_pessoa_resp_w, 
			ds_grupo_w 
		from	san_grupo_entidade 
		where	nr_sequencia = nr_seq_grupo_w;
	end if;
	 
	if (ie_opcao_p = 'R') then 
		ds_retorno_w := cd_pessoa_resp_w;
	elsif (ie_opcao_p = 'D') then 
		ds_retorno_w := ds_grupo_w;
	elsif (ie_opcao_p = 'C') then 
		ds_retorno_w := nr_sequencia_w;
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
