CREATE OR REPLACE FUNCTION obter_dados_grupo_pep (cd_pessoa_fisica_p text, nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_dados_w 	varchar(100);
ds_cor_w	varchar(15);
ds_grupo_w	varchar(60);


BEGIN

select	max(b.ds_cor),
		max(ds_grupo)
into STRICT	ds_cor_w,
		ds_grupo_w
from	pac_grupo_atend a,
		pac_grupo b
where	a.nr_seq_grupo = b.nr_sequencia
and		cd_pessoa_fisica = cd_pessoa_fisica_p
and 	nr_atendimento = nr_atendimento_p
and 	coalesce(dt_saida::text, '') = '';

if (coalesce(ds_grupo_w::text, '') = '') then
	select	max(b.ds_cor),
			max(ds_grupo)
	into STRICT	ds_cor_w,
			ds_grupo_w
	from	pac_grupo_atend a,
			pac_grupo b
	where	a.nr_seq_grupo = b.nr_sequencia
	and		cd_pessoa_fisica = cd_pessoa_fisica_p
	and 	coalesce(dt_saida::text, '') = '';
end if;

if (ie_opcao_p = 'C') then
	ds_dados_w := ds_cor_w;
elsif (ie_opcao_p = 'D') then
	ds_dados_w := ds_grupo_w;
end if;

return	ds_dados_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
