CREATE OR REPLACE FUNCTION obter_dados_grupo_segmento ( nr_sequencia_p bigint, ie_opcao_p bigint) RETURNS bigint AS $body$
DECLARE


/*ie_opcao_p
1 - cd_empresa
2 - cd_estabelecimento */
cd_empresa_w			bigint;
cd_estabelecimento_w		bigint;
ds_retorno_w			bigint;



BEGIN

select	cd_empresa,
	cd_estabelecimento
into STRICT	cd_empresa_w,
	cd_estabelecimento_w
from	grupo_segmento_compras
where	nr_sequencia = nr_sequencia_p;

if (ie_opcao_p = 1) then
	ds_retorno_w := cd_empresa_w;
elsif (ie_opcao_p = 2) then
	ds_retorno_w := cd_estabelecimento_w;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
