CREATE OR REPLACE FUNCTION obter_dados_hemocomponente_rep ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);

/*
	ie_opcao_p
	IF - IE_PERMITE_FILTRADO
	II - IE_PERMITE_IRRADIADO
	IA - IE_PERMITE_ALIQUOTADO
	IL - IE_PERMITE_LAVADO
	IE - IE_PERMITE_AFERESE
	IP - IE_PERMITE_POOL
*/
BEGIN

if (ie_opcao_p = 'IF') then
	select	coalesce(max(ie_permite_filtrado),'N')
	into STRICT	ds_retorno_w
	from	san_derivado
	where	nr_sequencia = nr_sequencia_p;
elsif (ie_opcao_p = 'II') then
	select	coalesce(max(ie_permite_irradiado),'N')
	into STRICT	ds_retorno_w
	from	san_derivado
	where	nr_sequencia = nr_sequencia_p;
elsif (ie_opcao_p = 'IA') then
	select	coalesce(max(ie_permite_aliquotado),'N')
	into STRICT	ds_retorno_w
	from	san_derivado
	where	nr_sequencia = nr_sequencia_p;
elsif (ie_opcao_p = 'IL') then
	select	coalesce(max(ie_permite_lavado),'N')
	into STRICT	ds_retorno_w
	from	san_derivado
	where	nr_sequencia = nr_sequencia_p;
elsif (ie_opcao_p = 'IE') then
	select	coalesce(max(ie_permite_aferese),'N')
	into STRICT	ds_retorno_w
	from	san_derivado
	where	nr_sequencia = nr_sequencia_p;
elsif (ie_opcao_p = 'IP') then
	select	coalesce(max(ie_permite_pool),'N')
	into STRICT	ds_retorno_w
	from	san_derivado
	where	nr_sequencia = nr_sequencia_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
