CREATE OR REPLACE FUNCTION obter_dados_hor_item_prescr (nr_seq_horario_p bigint, ie_tipo_item_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
H - Horário administrado.
*/
ds_retorno_w	varchar(255);
ds_horario_w	varchar(19);


BEGIN
if (nr_seq_horario_p IS NOT NULL AND nr_seq_horario_p::text <> '') then
	/* obter horário conforme tipo item */

	if (ie_tipo_item_p = 'D') then
		select	to_char(coalesce(dt_horario, clock_timestamp()),'dd/mm/yyyy hh24:mi:ss')
		into STRICT	ds_horario_w
		from	prescr_dieta_hor
		where	nr_sequencia	= nr_seq_horario_p
		and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

	elsif (ie_tipo_item_p in ('S','M','MAT','IAG','IA')) then
		select	to_char(coalesce(dt_horario, clock_timestamp()),'dd/mm/yyyy hh24:mi:ss')
		into STRICT	ds_horario_w
		from	prescr_mat_hor
		where	nr_sequencia	= nr_seq_horario_p
		and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

	elsif (ie_tipo_item_p in ('P','G','C','I')) then
		select	to_char(coalesce(dt_horario, clock_timestamp()),'dd/mm/yyyy hh24:mi:ss')
		into STRICT	ds_horario_w
		from	prescr_proc_hor
		where	nr_sequencia	= nr_seq_horario_p
		and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

	elsif (ie_tipo_item_p = 'R') then
		select	to_char(coalesce(dt_horario, clock_timestamp()),'dd/mm/yyyy hh24:mi:ss')
		into STRICT	ds_horario_w
		from	prescr_rec_hor
		where	nr_sequencia	= nr_seq_horario_p
		and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

	elsif (ie_tipo_item_p = 'E') then
		select	to_char(coalesce(dt_horario, clock_timestamp()),'dd/mm/yyyy hh24:mi:ss')
		into STRICT	ds_horario_w
		from	pe_prescr_proc_hor
		where	nr_sequencia	= nr_seq_horario_p;
	end if;

	if (coalesce(ie_opcao_p,'H') = 'H') then
		ds_retorno_w	:= ds_horario_w;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
