CREATE OR REPLACE FUNCTION obter_dados_hotel ( nr_sequencia_p bigint, ie_tipo_retorno_p text) RETURNS varchar AS $body$
DECLARE

 
/* 
Endere√ßo = 'E' 
Bairro = 'B' 
Cidade= 'C' 
*/
	 
					 
ds_endereco_w		varchar(265);
ds_bairro_w		varchar(255);
ds_cidade_w		varchar(255);


BEGIN 
 
select	max(substr(obter_dados_pf_pj(null,cd_cnpj_hotel,'EN'),1,255)) || ' - ' || max(substr(obter_dados_pf_pj(null,cd_cnpj_hotel,'NR'),1,255)), 
	max(substr(obter_dados_pf_pj(null,cd_cnpj_hotel,'B'),1,255)), 
	max(substr(obter_dados_pf_pj(null,cd_cnpj_hotel,'CI'),1,255)) 
into STRICT	ds_endereco_w, 
	ds_bairro_w, 
	ds_cidade_w 
from 	via_hotel 
where 	nr_sequencia = nr_sequencia_p;
 
if (ie_tipo_retorno_p = 'E') then 
	return ds_endereco_w;
elsif (ie_tipo_retorno_p = 'B') then 
	return ds_bairro_w;
elsif (ie_tipo_retorno_p = 'C') then 
	return ds_cidade_w;
end if;	
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
