SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_img_html (nr_sequencia_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w    varchar(4000) := null;
init            varchar(1);
qtd             bigint;


BEGIN

  if (ie_tipo_p = 'EXP_IMG') then
    select to_char(cd_exp_imagem) into STRICT ds_retorno_w
    from tasy_padrao_imagem_html where nr_sequencia = nr_sequencia_p;
  elsif (ie_tipo_p = 'PATH') then
    select count(1) into STRICT qtd
    from tasy_padrao_imagem_html where nr_sequencia = nr_sequencia_p;

    if (qtd > 0) then
      select ds_dir_img_server into STRICT ds_retorno_w
      from tasy_padrao_imagem_html where nr_sequencia = nr_sequencia_p;

      init := substr(ds_retorno_w, 1, 1);
      if (init = '/') then
        ds_retorno_w := 'assets' || ds_retorno_w;
      else
        ds_retorno_w := 'assets/' || ds_retorno_w;
      end if;
    end if;
  elsif (ie_tipo_p = 'LEG') then
    select ie_tipo_legenda into STRICT ds_retorno_w
    from tasy_padrao_imagem_html where nr_sequencia = nr_sequencia_p;
  elsif (ie_tipo_p = 'EXP_CON') then
    select to_char(cd_exp_conceito) into STRICT ds_retorno_w
    from tasy_padrao_imagem_html where nr_sequencia = nr_sequencia_p;
  end if;

  return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
