CREATE OR REPLACE FUNCTION obter_dados_inconsistencia (nr_seq_inconsistencia_p bigint, cd_estabelecimento_p bigint, cd_perfil_p bigint, ie_valor_p text, ie_campo_p text) RETURNS varchar AS $body$
DECLARE


IE_FECHA_CONTA_w		varchar(01);
IE_FECHA_ATENDIMENTO_w		varchar(01);
IE_FECHA_PROTOCOLO_w		varchar(01);
IE_FECHA_CONTA_estab_w		varchar(01);
IE_FECHA_ATENDIMENTO_estab_w	varchar(01);
IE_FECHA_PROTOCOLO_estab_w	varchar(01);
ie_valor_w			varchar(01);


BEGIN

select	max(IE_FECHA_CONTA),
	max(IE_FECHA_ATENDIMENTO),
	max(IE_FECHA_PROTOCOLO)
into STRICT	IE_FECHA_CONTA_estab_w,
	IE_FECHA_ATENDIMENTO_estab_w,
	IE_FECHA_PROTOCOLO_estab_w
from	inconsistencia_estab
where	cd_estabelecimento	= cd_estabelecimento_p
and	nr_seq_inconsistencia	= nr_seq_inconsistencia_p;


select	max(IE_FECHA_CONTA),
	max(IE_FECHA_ATENDIMENTO),
	max(IE_FECHA_PROTOCOLO)
into STRICT	IE_FECHA_CONTA_w,
	IE_FECHA_ATENDIMENTO_w,
	IE_FECHA_PROTOCOLO_w
from	inconsistencia_perfil
where	cd_estabelecimento	= cd_estabelecimento_p
and	cd_perfil		= cd_perfil_p
and	nr_seq_inconsistencia	= nr_seq_inconsistencia_p;


if (ie_campo_p	= 'IE_FECHA_CONTA') then
	ie_valor_w	:= coalesce(coalesce(IE_FECHA_CONTA_w,IE_FECHA_CONTA_estab_w), ie_valor_p);
elsif (ie_campo_p	= 'IE_FECHA_ATENDIMENTO') then
	ie_valor_w	:= coalesce(coalesce(IE_FECHA_ATENDIMENTO_w, IE_FECHA_ATENDIMENTO_estab_w), ie_valor_p);
elsif (ie_campo_p	= 'IE_FECHA_PROTOCOLO') then
	ie_valor_w	:= coalesce(coalesce(IE_FECHA_PROTOCOLO_w, IE_FECHA_PROTOCOLO_estab_w), ie_valor_p);
end if;

return	ie_valor_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
