CREATE OR REPLACE FUNCTION obter_dados_inconsist_farm ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_inconsistencia_w	varchar(255);


BEGIN

if (nr_sequencia_p > 0) then
	select	ds_inconsistencia
	into STRICT	ds_inconsistencia_w
	from	inconsistencia_med_farm
	where	nr_sequencia	=	nr_sequencia_p;

	if (ie_opcao_p = 'D') then
		ds_retorno_w	:=	ds_inconsistencia_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
