CREATE OR REPLACE FUNCTION obter_dados_indicacao ( nr_seq_indicacao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p
'P' - Solicita pessoa indicadora
'J' - Solicita pessoa jur√≠dica indicadora
*/
ie_pessoa_w		varchar(1);
ie_pessoa_juridica_w	varchar(1);
ds_retorno_w		varchar(1);


BEGIN

if (nr_seq_indicacao_p IS NOT NULL AND nr_seq_indicacao_p::text <> '') then
	begin
	select	max(a.ie_pessoa),
		max(a.ie_pessoa_juridica)
	into STRICT	ie_pessoa_w,
		ie_pessoa_juridica_w
	from	tipo_indicacao a
	where	a.nr_sequencia = nr_seq_indicacao_p;

	if (ie_opcao_p = 'P') then
		ds_retorno_w	:= ie_pessoa_w;
	elsif (ie_opcao_p = 'J') then
		ds_retorno_w	:= ie_pessoa_juridica_w;
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
