SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_inf_adic_agenda ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_informacoes_internacao_w	varchar(255);
ds_informacoes_internacao_ww	varchar(2000) := null;

/*
ie_opcao_p
II - Informações de internação
*/
c01 CURSOR FOR
	SELECT	substr(ds_informacoes_internacao,1,255)
	from	agenda_paciente_inf_adic
	where	nr_seq_agenda = nr_sequencia_p
	order by nr_sequencia;

BEGIN

open c01;
loop
fetch c01 into
	ds_informacoes_internacao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (coalesce(ds_informacoes_internacao_ww::text, '') = '') then
		ds_informacoes_internacao_ww := ds_informacoes_internacao_w;
	else
		ds_informacoes_internacao_ww := substr(ds_informacoes_internacao_ww || ' ' ||ds_informacoes_internacao_w,1,2000);
	end if;
end;
end loop;
close c01;

return	ds_informacoes_internacao_ww;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
