CREATE OR REPLACE FUNCTION obter_dados_inspecao_regra_mat ( cd_material_p bigint, cd_estabelecimento_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*ie_opcao_p
	DV	qt_dias_validade
	TTI	qt_temp_min_transp
	TTA	qt_temp_max_transp
*/
qt_retorno_w		integer;
qt_dias_validade_w	integer;
qt_temp_min_transp_w	integer;
qt_temp_max_transp_w	integer;


BEGIN
select	max(qt_dias_validade),
	max(qt_temp_min_transp),
	max(qt_temp_max_transp)
into STRICT	qt_dias_validade_w,
	qt_temp_min_transp_w,
	qt_temp_max_transp_w
from	inspecao_regra_material
where	cd_material = cd_material_p
and	cd_estabelecimento = cd_estabelecimento_p;

if (ie_opcao_p = 'DV') then
	qt_retorno_w := qt_dias_validade_w;
elsif (ie_opcao_p = 'TTI') then
	qt_retorno_w := qt_temp_min_transp_w;
elsif (ie_opcao_p = 'TTA') then
	qt_retorno_w := qt_temp_max_transp_w;
end if;


return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
