SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_integracao_laudo ( ds_opcao_p text, ie_valor_p text default null) RETURNS varchar AS $body$
DECLARE

			
/*
ds_opcao_p

NMU      NM_USUARIO_PACS
DSS      DS_SENHA_PACS
DIR_PAC  DS_DIRETORIO_PACS

*/
			
		
ds_retorno_w			parametro_medico.ds_diretorio_pacs%type;
cd_estabelecimento_w 	bigint;


BEGIN

cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;

if (ds_opcao_p = 'DIR_PAC') then

	select  max(ds_diretorio_pacs)
	into STRICT	ds_retorno_w
	from    parametro_medico
	where   cd_estabelecimento = cd_estabelecimento_w;
	

elsif (ds_opcao_p = 'NMU') then

	select  max(nm_usuario_pixeon)
	into STRICT	ds_retorno_w
	from    parametro_integracao_pacs
	where   cd_estabelecimento  = cd_estabelecimento_w;
	
elsif (ds_opcao_p = 'DSS') then

	select  max(ds_senha_pixeon)
	into STRICT	ds_retorno_w
	from    parametro_integracao_pacs
	where   cd_estabelecimento  = cd_estabelecimento_w;
elsif (ds_opcao_p = 'CPOE_INT') then

	select	max(cd_intervalo_cpoe)
	into STRICT	ds_retorno_w
	from	parametro_medico
	where   cd_estabelecimento  = cd_estabelecimento_w;
	
else
	ds_retorno_w := '';	
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
