CREATE OR REPLACE FUNCTION obter_dados_intens_pero ( nr_seq_material_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_agente_w		bigint;
ds_retorno_w		varchar(255);


BEGIN

select	nr_seq_agente
into STRICT	nr_seq_agente_w
from 	agente_anest_material
where	nr_sequencia = nr_seq_material_p;

if (ie_opcao_p = 'DTD') then
	ds_retorno_w := obter_dados_agente(nr_seq_agente_w,'DTD');
elsif (ie_opcao_p = 'UM') then
	ds_retorno_w := substr(obter_dados_agente(nr_seq_agente_w,'UM'),1,100);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
